import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image} from 'react-native';

const PostTab = () => {
  const [liked, setLiked] = useState(false);
  const [disliked, setDisliked] = useState(false);
  const [likeCount, setLikeCount] = useState(120);
  const [dislikeCount, setDislikeCount] = useState(20);
  const [commentsCount, setCommentsCount] = useState(9);


  const handleLike = () => {
    setLiked(!liked);
    if (disliked) {
      setDisliked(false);
      setDislikeCount(dislikeCount - 1);
    }
    setLikeCount(liked ? likeCount - 1 : likeCount + 1);
  };

  const handleDislike = () => {
    setDisliked(!disliked);
    if (liked) {
      setLiked(false);
      setLikeCount(likeCount - 1);
    }
    setDislikeCount(disliked ? dislikeCount - 1 : dislikeCount + 1);
  };

  const handleComment = () => {
    // add logic for handling comments
  };

  const handleShare = () => {
    // add logic for handling share
  };

  return (
    <View style={styles.container}>
      
      <TouchableOpacity onPress={handleLike}>
        <View style={styles.row}>
        {liked ? (
          <Image source={require('../assets/Like.png')} style={styles.iconlike} />
        ) : (
          <Image source={require('../assets/Likeinactive.png')} style={styles.iconlike} />
        )}
        <Text style={styles.count}>{likeCount}</Text>
        </View>
      </TouchableOpacity>


      <TouchableOpacity onPress={handleDislike}>
      <View style={styles.row}>
        {disliked ? (
          <Image source={require('../assets/dislike.png')} style={styles.icondislike} />
        ) : (
          <Image source={require('../assets/dislikeinactive.png')} style={styles.icondislike} />
        )}
        <Text style={styles.count}>{dislikeCount}</Text>
        </View>
      </TouchableOpacity>


      <TouchableOpacity onPress={handleComment}>
        <View style={styles.row}>
           <Image source={require('../assets/comment.png')} style={styles.iconcomment} />
           <Text style={styles.count}>{commentsCount}</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity onPress={handleShare}>
        <Image source={require('../assets/share.png')} style={styles.iconshare} />
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    // width: 350,
    justifyContent: 'space-around',
    marginTop: 10,
  },
  like:{
    flexDirection: 'row',
  },
  count: {
    fontSize: 13, fontWeight: '500', color: '#a0a0a0',
    marginLeft: 5, marginTop: 4,
  },
  iconlike: {
    width: 22,
    height: 22,
  },
  icondislike: {
    width: 25,
    height: 25,
  },
  iconcomment: {
    width: 22,
    height: 24,
    
  },
  iconshare: {
    width: 20,
    height: 20,
  },
  row:{
    flexDirection: 'row',
  }
});

export default PostTab;
