import { View, Image, StyleSheet, Text, StatusBar} from "react-native";
import { Picker } from "@react-native-picker/picker";
import { MaterialIcons } from '@expo/vector-icons';
import { useSelector, useDispatch } from 'react-redux';
import { SET_CAMPUS_FILTER } from '../src/store/store';
import { useEffect, React } from "react";
import { FontAwesome5 } from '@expo/vector-icons';

const Topnav = () => {
  const dispatch = useDispatch();
  const campus = useSelector((state) => state.campus.campus);

  const handleCampusChange = (selectedCampus) => {
    console.log("Selected campus value:", selectedCampus);
    dispatch({ type: "SET_CAMPUS_FILTER", payload: selectedCampus });
  };

  let activeIcon;
  switch (campus) {
    case 'inter-campus':
      activeIcon = <FontAwesome5 name="globe" size={18} color="#000" />;
      break;
    case 'my-campus':
      activeIcon = <FontAwesome5 name="location-on" size={18} color="#000" />;
      break;
    case 'my-faculty':
      activeIcon = <FontAwesome5 name="school" size={18} color="#000" />;
      break;
    default:
      activeIcon = <FontAwesome5 name="globe" size={18} color="#000" />;
  }

  return (
    <View style={styles.header}>
      <StatusBar barStyle={'dark-content'} backgroundColor={'white'} />
      <View style={styles.topnav}>
        <Image
          style={styles.downloadRemovebgPreview2Icon}
          resizeMode="cover"
          source={require('../assets/downloadremovebgpreview-2.png')}
        />
        <Text style={styles.multimediaUniversity}>{`Multimedia\nUniversity`}</Text>

        <View style={styles.filter}>
          <Picker
            selectedValue={campus}
            onValueChange={handleCampusChange}
            icon={() => (
              <MaterialIcons
                name="filter-list"
                size={24}
                color="#000"
                style={styles.icon}
              />
            )}
          >
            <Picker.Item label={'All'} value="inter-campus" />
            <Picker.Item label={'My Campus'} value="my-campus" />
            <Picker.Item label={'My Faculty'} value="my-faculty" />
          </Picker>
          <View style={styles.activeFilter}>{activeIcon}</View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  downloadRemovebgPreview2Icon: {
    width: 40,
    height: 31,
  },
  filter:{
   width: 80,
   marginLeft: 100,

  },
  multimediaUniversity: {
    fontSize: 20,
    fontWeight: 600,
    FontFamily: '',
    color: 'black',
    textAlign: "left",
    marginLeft: 20,
  },
  topnav: {
    backgroundColor: 'white',
    width: 393,
    flexDirection: "row",
    padding: 10,
  },
  header: {
    top: 0,
    left: 0,   
  },
});

export default Topnav;
