import React, { useState } from 'react';
import { View, TextInput, Button, Image } from 'react-native';

const Tweet = () => {
  const [message, setMessage] = useState('');
  const [username, setUsername] = useState('JohnDoe'); // replace with user's username
  const [profileImage, setProfileImage] = useState('../assets/nastyblak3@3x.png'); // replace with user's profile image URL

  const handleSubmit = () => {
    console.log(`Sending message '${message}' from ${username} with profile image ${profileImage}`);
    
    setMessage('');
  };

  return (
    <View>
      <Image source={{ uri: profileImage }} style={{ width: 50, height: 50 }} />
      <TextInput
        placeholder="Type a message..."
        value={message}
        onChangeText={setMessage}
      />
      {message ? (
        <Button title="Submit" onPress={handleSubmit} />
      ) : null}
    </View>
  );
};

export default Tweet;
