import * as React from "react";
import { View, Image, StyleSheet, Pressable, TextInput, Text, KeyboardAvoidingView, StatusBar, TouchableOpacity, ActivityIndicator} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Border, Color, FontFamily, Padding, FontSize } from "../GlobalStyles";
import { getAuth, signInWithEmailAndPassword, setPersistence, browserSessionPersistence } from "firebase/auth";
import { useState, useEffect } from "react";
import { initializeApp } from 'firebase/app';
import { getFirestore, doc, getDoc } from "firebase/firestore";



const firebaseConfig = {
  apiKey: "AIzaSyA7JPJ0jkVgBqd_H46H4ODpCuYt6RHU-zI",
  authDomain: "testampus.firebaseapp.com",
  projectId: "testampus",
  storageBucket: "testampus.appspot.com",
  messagingSenderId: "175187994196",
  appId: "1:175187994196:web:df2f335c5922e2ddd1afc1",
  measurementId: "G-BB8S9ZJNWL"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

const Landing = () => {
  const navigation = useNavigation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");



  const db = getFirestore();

  const [isLoading, setLoading] = useState(false);
  const handleSubmit = async () => {
    setLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const uid = userCredential.user.uid;



      const userProfileDoc = doc(db, "userProfiles", uid);
      const userProfile = await getDoc(userProfileDoc);
      // console.log("UID:", uid); // Log the UID
      if (userProfile.exists()) {
        navigation.navigate("BottomTabsRoot", {
          screen: "Feed",
          params: { uid },
        });

      } else {
        navigation.navigate("CreateProfile");
      }
    } catch (error) {
      console.log(error);
      setErrorMsg("Incorrect email or password. Please try again.");
    }
    setLoading(false);
  };

  return (
    <View style={styles.landing}>
      <StatusBar barStyle={'dark-content'} backgroundColor={'white'}/>
      <View style={styles.beautifulSplash}>
        <Image
          style={[styles.mmuHotel21, styles.mmuHotel21Layout]}
          resizeMode="cover"
          source={require("../assets/mmu-hotel2-1.png")}
        />
        <Image
          style={[styles.swimmu1Icon, styles.iconLayout]}
          resizeMode="cover"
          source={require("../assets/swimmu-1.png")}
        />
        <Image
          style={[styles.mmu11Icon, styles.iconLayout]}
          resizeMode="cover"
          source={require("../assets/mmu1-1.png")}
        />
        <Image
          style={[styles.mmustud1Icon, styles.iconPosition]}
          resizeMode="cover"
          source={require("../assets/mmustud-1.png")}
        />
        <Image
          style={[styles.mmuhotel1Icon, styles.iconPosition]}
          resizeMode="cover"
          source={require("../assets/mmuhotel-1.png")}
        />
        <Image
          style={[styles.whatsappImage20230119At9, styles.whatsappPosition]}
          resizeMode="cover"
          source={require("../assets/whatsapp-image-20230119-at-943-1.png")}
        />
        <Image
          style={[styles.whatsappImage20230119At91, styles.whatsappPosition]}
          resizeMode="cover"
          source={require("../assets/whatsapp-image-20230119-at-943-2.png")}
        />
      </View>

{/* Form..................................................... */}
     <KeyboardAvoidingView>
     {errorMsg ? <Text style={styles.error}>{errorMsg}</Text> : null}
      <View style={[styles.registrationNumberWrapper, styles.registrationFlexBox]}>
        <TextInput
         style={styles.inputbox}
          placeholder="Registration number"
          onChangeText={(text) => setEmail(text)}
          theme={{
            fonts: { regular: { fontFamily: "", fontWeight: "Regular" } },
            colors: { text: "#000" },
          }}
        />
      </View>
      <View style={[styles.password, styles.registrationFlexBox]}>
        <TextInput
        style={styles.inputbox}
          placeholder="Password"
          secureTextEntry={true}
          onChangeText={(text) => setPassword(text)}
          theme={{
            fonts: { regular: { fontFamily: "", fontWeight: "Regular" } },
            colors: { text: "#000" },
          }}
        />
      </View>
      </KeyboardAvoidingView>


      <Pressable
        style={[styles.registration, styles.registrationFlexBox]}
        onPress={handleSubmit}
      >
        <Text style={styles.continue}>Continue</Text>
      </Pressable>
      {isLoading && (
        <View style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <ActivityIndicator />
        </View>
      )}


      <Image style={styles.vibeIcon} resizeMode="cover" source={require("../assets/graduate.png")}/>
      <View style={styles.university}>
        <Text style={[styles.multimediaUniversity, styles.continueTypo]} >{`Multimedia University`}</Text>

      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mmuHotel21Layout: {
    width: 125,
    left: 0,
  },
  error:{
    fontSize: 15, fontWeight: 600, 
    top: 520, left: 50, color: 'red',
  },
  okbtn:{
    padding: 20,
    color: 'white'
  },
  inputbox: {
    backgroundColor: '#f8f8f8',
    padding: 15,
    borderRadius: 20,
  },
  iconLayout: {
    width: 120,
    left: 131,
  },
  iconPosition: {
    left: 258,
    width: 125,
    position: "absolute",
  },
  whatsappPosition: {
    borderTopRightRadius: Border.br_mini,
    position: "absolute",
  },
  registrationFlexBox: {
    position: "absolute",

  },
  continueTypo: {
    fontFamily: FontFamily.interBold,
    fontWeight: "700",
  },
  mmuHotel21: {
    height: 200,
    borderBottomRightRadius: Border.br_mini,
    position: "absolute",
    top: 0,
  },
  swimmu1Icon: {
    height: 100,
    borderBottomLeftRadius: Border.br_mini,
    top: 0,
    borderBottomRightRadius: Border.br_mini,
    position: "absolute",
  },
  mmu11Icon: {
    top: 108,
    borderRadius: Border.br_mini,
    height: 200,
    position: "absolute",
  },
  mmustud1Icon: {
    borderBottomLeftRadius: Border.br_mini,
    top: 0,
    height: 200,
  },
  mmuhotel1Icon: {
    top: 207,
    height: 199,
    borderTopLeftRadius: Border.br_mini,
  },
  whatsappImage20230119At9: {
    top: 209,
    height: 197,
    width: 125,
    left: 0,
  },
  whatsappImage20230119At91: {
    top: 316,
    height: 90,
    borderTopLeftRadius: Border.br_mini,
    width: 120,
    left: 131,
  },
  beautifulSplash: {
    left: 6,
    width: 383,
    height: 406,
    top: 0,
    position: "absolute",
  },
  registrationNumberWrapper: {
    top: 550,
    width: 300,
    backgroundColor: '#f8f8f8',
    left: 43,
    borderRadius: 20,
  },
  password: {
    top: 620,
    width: 300,
    left: 43,
  },
  continue: {
    textAlign: "left",
    fontWeight: "700", color: 'white',
  },
  registration: {
    top: 720,
    backgroundColor: Color.steelblue_200,
    paddingHorizontal: 118, paddingVertical: 10,
    alignItems: "center",
    borderRadius: Border.br_xl,
    left: 43,
  },
  vibeIcon: {
   width: 100, height: 100, alignSelf: 'center', 
   top: 340, borderWidth: 1, borderColor: '#2C96B8', 
   borderRadius: 100,
  },
  multimediaUniversity: {
    fontSize: 30,
    color: Color.black,
    width: 179,
    height: 65,
    textAlign: "left",
  },
  university: {
    top: 461,
    left: 107,
    alignItems: "flex-end",
    justifyContent: "flex-end",
    position: "absolute",
  },
  landing: {
    backgroundColor: Color.white,
    flex: 1,
    width: "100%",
    height: 852,
    overflow: "hidden",
  },
});

export default Landing;
