import React, { useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet, Dimensions} from "react-native";
import { MaterialIcons } from "@expo/vector-icons";

const {width, height} = Dimensions.get('window');


const ProfessionalBusiness = ({ subscription }) => {
  const [activeTab, setActiveTab] = useState("catalogue");
  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
  };

  return (
    <View style={styles.container}>
      <Text>My business</Text>
      <View style={styles.header}>
        <Text style={[styles.text,styles.businessName]}>Business name</Text>
        <Text style={[styles.text , styles.contacts]}>_2547.......c</Text>
        <Text style={[styles.text, styles.type]}>Fashion & Design</Text>
      </View>

      <View style={styles.menu}>
        <TouchableOpacity
          style={[styles.menuItem,activeTab === "catalogue" && styles.activeMenuItem,]}
          onPress={() => handleTabChange("catalogue")}
         >
          <Text style={styles.menuText}>Catalogue</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.menuItem,activeTab === "analytics" && styles.activeMenuItem,]}
          onPress={() => handleTabChange("analytics")}
         >
          <Text style={styles.menuText}>Analytics</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.menuItem,activeTab === "advertising" && styles.activeMenuItem]}
          onPress={() => handleTabChange("advertising")}
         >
          <Text style={styles.menuText}>Advertising</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.menuItem,activeTab === "edit" && styles.activeMenuItem]}
          onPress={() => handleTabChange("edit")}
         >
          <Text style={styles.menuText}>Edit</Text>
        </TouchableOpacity>
      </View>

      {activeTab === "catalogue" && (
        <View style={styles.content}>
          <Text style={styles.headerText}>Catalogue</Text>
          <Text style={styles.normalText}>Create your business catalogue and showcase your products to potential customers</Text>
        </View>
      )}

      {activeTab === "analytics" && (
        <View style={styles.content}>
          <Text style={styles.headerText}>Analytics</Text>
          <Text style={styles.normalText}>View analytics of your posts and products to track engagement and sales</Text>
        </View>
      )}

      {activeTab === "advertising" && (
        <View style={styles.content}>
          <Text style={styles.headerText}>Advertising</Text>
          <Text style={styles.normalText}>Create custom adverts to promote your business and reach more potential customers</Text>
        </View>
      )}

      {activeTab === "edit" && (
        <View style={styles.content}>
          <Text style={styles.headerText}>Edit</Text>
        </View>
      )}

    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  header: {
    marginVertical: 5,
    borderWidth: 1, borderRadius: 15, borderColor: '#d0d0d0',
    alignItems: 'center', justifyContent: 'center',
    height: height * 0.2,
  },
  businessName: {
    fontSize: 16,
    fontWeight: "bold",
  },
  type:{
    color: '#a0a0a0'
  },
  menu: {
    flexDirection: "row",
    marginBottom: 20,
    justifyContent: 'space-between'
  },
  menuItem: {
    padding: 10,
    borderRadius: 10,
    backgroundColor: "#f0f0f0",
  },
  activeMenuItem: {
    backgroundColor: "#ff8c00",
  },
  menuText: {
    fontWeight: '500'
  },
  content: {
    flex: 1,
  },
  headerText: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 10,
  },
  normalText: {
    fontSize: 14,
  },
  text:{
    marginVertical: 5
  }
});

export default ProfessionalBusiness;
