import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, Modal, StyleSheet } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import SearchResults from './searchResults';

const Search = () => {
  const [query, setQuery] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  const handleSearch = (text) => {
    setQuery(text);
  };

  const handleTextChange = (text) => {
    setQuery(text);
    if (text.length >= 3) {
      setModalVisible(true);
    }
  };
  // Clear set query
  const clearSearch = (text) => {
    setQuery('');
  }
// close modal
  const handleCloseModal = () => {
    setQuery('');
    setModalVisible(false);
  };

  return (
    <View style={styles.container}>
        <View style={styles.bar}>
        <View style={styles.searchBar}>
           <TouchableOpacity onPress={() => setModalVisible(true)}>
            <MaterialIcons name="search" size={24} color='rgba(38,150,184, 0.5)'style={styles.search} />
           </TouchableOpacity>
          <TextInput
            placeholder="Search"
            style={styles.searchInput}
            value={query}
            onChangeText={handleTextChange}
          />
          </View>
        </View>


      <Modal
        animationType="slide"
        transparent={false}
        visible={modalVisible}
      >
      <View style={styles.modal}>
        <View style={styles.header}>
          <TouchableOpacity onPress={handleCloseModal}>
            <MaterialIcons name="arrow-back" size={25} color="#404040" />
           </TouchableOpacity>

           <View style={styles.modalsearchbar}>
             <MaterialIcons name="search" size={24} color='rgba(38,150,184, 0.5)' style={styles.search} />
             <TextInput
               placeholder="Search"
               style={styles.searchInput}
               value={query}
               onChangeText={handleSearch}
              />
             <TouchableOpacity onPress={clearSearch} style={styles.clear}>
                 <MaterialIcons name="cancel" size={24} color="#999" />
             </TouchableOpacity>
           </View>
           </View>

          <SearchResults query={query}/>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {

  },
  modal: {
    flex: 1,
    paddingTop: 50,
    paddingHorizontal: 20,
    backgroundColor: '#ffff',
  },
  bar:{
    padding: 5, backgroundColor: 'white', marginBottom: 3,
    paddingVertical: 8,
    alignItems: 'center'
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#fff',
    paddingHorizontal: 10,
    width: '90%', 
    borderWidth: 1, borderColor: '#e0e0e0', borderRadius: 10,
  },
  modalsearchbar: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,marginTop: 20,
    paddingVertical: 3,
    borderWidth: 1, borderColor: '#e0e0e0', borderRadius: 10,
    backgroundColor: '#f8f8f8',
    width: 250,
    marginBottom: 5, marginLeft: 15,
  },
  header:{
    position: 'absolute',
    borderBottomColor: '#e0e0e0', borderBottomWidth: 1,
    left: 0, right: 0,
    alignItems: 'center', flexDirection: 'row',
    paddingHorizontal: 15,
  },
  searchInput: {
    marginLeft: 10,
    padding: 5,
  },
  clear:{
    right: 10,
    position: 'absolute'
  },
  search:{
    borderBottomColor: 'rgba(38,150,184, 0.5)',
    borderBottomWidth: 1,
    borderRadius: 10,
  }
});

export default Search;
