import * as React from "react";
import {
  View,
  StyleProp,
  ViewStyle,
  Text,
  StyleSheet,
  ImageBackground,
  Pressable,
  TouchableOpacity,
} from "react-native";
import Tabs from 'react-native-tabs';
import { useNavigation } from "@react-navigation/native";
import { Color, FontSize, FontFamily, Border, Padding } from "../GlobalStyles";
import PostTab from "./LikeTab";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const Snippet = ({ style }) => {
  const navigation = useNavigation();

  return (
    <View style={[styles.snippet, style]}>
       <View style={styles.topicTime}>
          <Text style={styles.topic}>
            <Icon name="fire" size={30} color="orange" />
            {'Do you love to party'}
            </Text>
          <Text style={styles.time}>
            {'time'}
            </Text>
      </View>

      <View style={styles.userProfile}>
          <ImageBackground 
              style={styles.profileImage} 
              resizeMode="cover"
              source={require("../assets/whatsappimage20230118at1101.png")} 
            />
          <View style={styles.usernameField}>
             <Text style={styles.username}>{"Daysterious"}</Text> 
             <Text style={styles.field}> {"Analytical chemistry"}</Text>
           </View>    
      </View>

      <Pressable onPress={() => navigation.navigate("Post")} >
         <View style={styles.messageFlexBox}>
           <Text style={styles.message} >
              {`Kama kawaida sherehe ibada. I want to inform you, this coming friday all roads head to Siron pool party......you just cant missthis!!! Attending artists: mejja`}
            </Text>
         </View>
       </Pressable>

      <PostTab/>
    </View>
  );
};

const styles = StyleSheet.create({
  snippet: {
    backgroundColor: 'white',
    padding: 10, paddingBottom: 20,
    borderBottomColor: '#f0f0f0', borderBottomWidth: 1,
  },
  topicTime: {
    alignItems: "center",
    flexDirection: "row",
    flex: 1,
    marginBottom: 10, marginLeft: 5, top: 0,
  },
  topic: {
    fontSize: 15,
    fontWeight: "500",
     width: '85%', 
  },
  time: {
    color: '#707070',
    fontSize: 12,
    fontWeight: "400",
  },
  username:{
    fontWeight: '500',
    marginLeft: 5,
  },
  field:{
    color: '#404040', marginLeft: 1,
    marginTop: 5,
  },
  usernameField:{
    alignItems: 'center',

  },

  messageFlexBox: {
    marginTop: 10,
  },
  profileImage: {
    borderRadius: 100,
    width: 45,
    height: 45,
  },
  usernameField: {
    marginLeft: 6,
  },
  userProfile: {
    marginTop: 5,marginLeft: 5,
    flexDirection: 'row',
  },
  message: {
    fontSize: 14, 
  },
  simmu22Icon: {
    borderRadius: Border.br_3xs,
    width: 349,
    height: 158,
  },
  image: {
    padding: Padding.p_8xs,
    marginTop: 5,
  },
  customTopTabChild: {
    display: "none",
  },
  customTopTabItem: {
    marginLeft: 55,
    display: "none",
  },
  customTopTab: {
    alignItems: "flex-end",
    marginTop: 5,
  },

});

export default Snippet;
