import { View, Image, StyleSheet, Text, StatusBar, TouchableOpacity} from "react-native";
import { useSelector, useDispatch } from 'react-redux';
import { SET_CAMPUS_FILTER } from '../src/store/store';
import { useEffect, React , useState} from "react";
import CampusModal from "./filterTopButton";

const Topnav = () => {
  const campus = useSelector(state => state.campus);
// console.log(campus.campus, 'is campus')
  return (
    <View style={styles.header}>
      <StatusBar barStyle={'dark-content'} backgroundColor={'white'} />
      <View style={styles.topnav}>
        <Image
          style={styles.icon}
          resizeMode="cover"
          source={require('../assets/downloadremovebgpreview-2.png')}
        />
        <Text style={styles.multimediaUniversity}>{
        campus.campus
        .replace(' ', '\n')
        }</Text>

      </View>
      <View style={styles.right}><CampusModal /></View>
    </View>
  );
};

const styles = StyleSheet.create({
  icon: {
    width: 40,
    height: 31,
  },
  multimediaUniversity: {
    fontSize: 20,
    fontWeight: 600,
    FontFamily: '',
    color: 'black',
    textAlign: "left",
    marginLeft: 10,
  },
  topnav: {
    flexDirection: "row",
    padding: 10,
  },
  header: {
    top: 0,   backgroundColor: 'white', right:0,
    left: 0,   flexDirection: 'row', width: '100%',
  },
  right:{
    right: 10,
    position: 'absolute'
  }

});

export default Topnav;
