import React, { useState, useEffect } from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity, ImageBackground} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Topnav from "../components/Topnav";
import { useNavigation } from '@react-navigation/native';
import { useSelector, useDispatch } from 'react-redux';
import Courses from "../components/courses";
import { LinearGradient } from 'expo-linear-gradient';
import {db} from '../firebase/firebase';
import { collection, onSnapshot, doc, where } from "firebase/firestore";
import { FontAwesome5 } from '@expo/vector-icons';
import Settings from "./Settings";


const Account = () => {
  const navigation = useNavigation();
  // Default user details
  const defaultProfileImage = require("../assets/graduate.png");
  const defaultUsername = "Student";
  const defaultField = "Guest";
  const defaultCampusProfile = require('../assets/campus.jpg')
  // Fetch details
  const userProfile = useSelector((state) => state.userProfile);
  const campus = useSelector(state => state.campus);
  const courseCode = `${campus.faculty}${campus.course}`
  const courseName = Courses[courseCode];
  const [campusProfile, setCampusProfile] = useState('');
  const [promoMessage, setPromoMessage] = useState(null);
  const [followersCount, setFollowersCount] = useState(0);
  const [followingCount, setFollowingCount] = useState(0);
  
console.log(campusProfile)
// Fetch followers and following
useEffect(() => {
  const unsubscribeFollowers = onSnapshot(collection(db, "userProfiles", userProfile.userId, "followers"),
    (snap) => setFollowersCount(snap.size));
  const unsubscribeFollowing = onSnapshot( collection(db, "userProfiles", userProfile.userId, "following"),
    (snap) => setFollowingCount(snap.size));
  return () => {unsubscribeFollowers();unsubscribeFollowing();};}, [userProfile.userId]);

  // Fetch campus profileImage
  useEffect(() => {
    const unsubscribe = onSnapshot( doc(collection(db, "campus"), campus.id),
      (docSnap) => {setCampusProfile(docSnap.data()?.profile || null); });
    return () => unsubscribe(); }, [campus.id]);

  // Fetch promotional messages
  useEffect(() => {
    const userId = userProfile.userId;
    const userProfilesRef = doc(db, "userProfiles", userId);
    const unsubscribe = onSnapshot(userProfilesRef, (doc) => {const data = doc.data();const promoMessages = data.promo || [];
      if (promoMessages.length > 0) {
        const latestPromoMessage = promoMessages[promoMessages.length - 1]; setPromoMessage(latestPromoMessage);}
    });
    return unsubscribe;}, [userProfile.userId]);

  //Set details
  const [username, setUsername] = useState(defaultUsername);
  const [field, setField] = useState(defaultField);
  const [profileImage, setProfileImage] = useState(defaultProfileImage);
  const [about, setAbout] = useState('I am a studentI am a studentI am a studentI am a studentI am a studentI am a student');
  
  // Set year and Student account type
  const currentYear = new Date().getFullYear();
  const startYear = campus.yearOfAdmission;
  const userStatus = currentYear > endYear ? "Alumni" : "Active";
  const endYear = Number(startYear) + 4;
  let joined = "";
  if (userProfile.userType === "Student") {
    joined = `Joined: ${startYear} - ${endYear}`;
  } else if (campus.field === "Service") {
    joined = `Joined: ${startYear} to date`;
  }


  useEffect(() => {
    setUsername(userProfile.username || defaultUsername);
    setField(courseName);
    setProfileImage(
      userProfile.profileImage ? { uri: userProfile.profileImage } : defaultProfileImage
 );
  }, [userProfile]);

  function handleCourse(){

  }

  return (
    <View style={styles.container}>
          
         <ImageBackground
           source={{ uri: campusProfile } || defaultCampusProfile }
           style={styles.topbar}>
           <LinearGradient
              colors={['rgb(255, 255, 255)', 'rgba(38,150,184, 0)']}
              style={styles.gradient}>
             <View style={styles.topnav}>
               <Image style={styles.icon} source={require('../assets/downloadremovebgpreview-2.png')}/>
               <Text style={styles.campus}>{ campus.campus .replace(' ', '\n')}</Text>
                <View style={styles.setting}>
                  <Settings/>
                </View>
             </View>
            
           </LinearGradient>
         </ImageBackground>
      
       


       <View style={styles.userFollow}>
       {/* User Profile */}
       <View style={styles.userProfile}>
        <View style={styles.overlay}>
            <Image source={profileImage} style={styles.profileImage}/>
            <TouchableOpacity style={styles.status}>
              <Text style={styles.statusText}>{userStatus}</Text>
            </TouchableOpacity>
         </View>
         <View style={styles.user}>
           <Text style={styles.username}>{username}</Text>
           <TouchableOpacity onPress={handleCourse}>
              <Text style={styles.field}>{field}</Text>
           </TouchableOpacity>
         </View>
         <Text numberOfLines={3}  style={styles.about}>{about}</Text>
         <View style={styles.joinflex}>
           <FontAwesome5 name="calendar-alt" color="#c0c0c0" size={14}/>
           <Text style={styles.joined}>{joined}</Text> 
          </View>
        
       </View>
       {/* Followers and following */}
       <View style={styles.followContainer}>
         <View style={styles.contain}> 
           <Text style={styles.count}>{'0'}</Text>
           <Text style={styles.placeholder}>Topics</Text>
         </View>
         <View style={styles.contain}>
           <Text style={styles.count}>{followersCount}</Text>
           <Text style={styles.placeholder}>Followers</Text>
         </View>
         <View style={styles.contain}>
           <Text style={styles.count}>{followingCount}</Text>
           <Text style={styles.placeholder}>Following</Text>
         </View>

        </View>
      </View>

     <View style={styles.buttoncontainer}>
       <TouchableOpacity style={styles.button}>
         <Text style={styles.buttonText}>Edit Profile</Text>
       </TouchableOpacity>
       <TouchableOpacity  style={styles.button}>
         <Text style={styles.buttonText}>Share Profile</Text>
       </TouchableOpacity>
      </View>

      
       {/* Platform messages and infor/ promotional/ resourceful */}
       {promoMessage && (
         <View style={styles.promoMessageContainer}>
           <View style={styles.iconContainer}>
             <Image source={promoMessage.icon} style={styles.icon} />
           </View>
           <View style={styles.messageContainer}>
             <Text style={styles.topic}>{promoMessage.topic}</Text>
             <Text style={styles.message}>{promoMessage.message}</Text>
             <TouchableOpacity style={styles.button}>
                <Text style={styles.buttonText}>Learn More</Text>
             </TouchableOpacity>
           </View>
         </View>
       )}
       {/* Promo message end */}


    </View>
  );
};


const styles = StyleSheet.create({
  gradient:{
    alignItems:'flex-start',
    left:0, right: 0,
    height: 100,
  },
  topnav: {
    flexDirection: "row",
    padding: 5,
    borderRadius: 5, 
    left:0, right: 0, 
    position: 'absolute',
  },
  setting:{
    position:'absolute',
    right: 20, top: 10
  },
  campus:{
    fontWeight:'600', fontSize: 20, color: '#404040', marginLeft: 10
  },
  userFollow:{
    flexDirection: 'row',
    width: '100%',height: 160,
  },
  overlay:{
    flexDirection: 'row', 
  },
  profileImage:{
    width: 80, height: 80,
    borderRadius: 100,
    borderWidth: 3, borderColor: 'white',
    left: 20
  },
  status:{
    alignSelf: 'center', left: -58, borderRadius: 100,
    backgroundColor: 'rgba(38,150,184, 0.3)', width: 75, height: 75,

  },
  statusText:{
    alignSelf: "center", top: 25, color: 'white'
  },
  userProfile:{
    top: -40,
    width: '40%'
  },
  user:{
    // alignItems: 'center',
    left: 10,
  },
  username:{
    fontWeight: '600', fontSize: 15,
    left: 10,
  }, 
  field:{
    color: '#2696b8', paddingVertical: 3,
  },
  about:{
    left: 10,
    marginTop: 3, 
  },
  joinflex:{
    flexDirection: 'row',
    marginTop: 5,  left: 10,
  },
  joined:{
    color: '#c0c0c0',
   marginLeft: 4
  },
  followContainer:{
    marginTop: 20, height: 70,
    flexDirection: 'row',
    width: '55%', 
    justifyContent: 'space-between', 
  },
  contain:{
    alignItems: 'center',
     height: 50,
  },
  placeholder:{
    fontSize: 16,
  },
  count:{
    fontWeight: '600', fontSize: 17
  },
  buttoncontainer:{
    flexDirection:'row', justifyContent: 'space-between',
    paddingHorizontal: 20,
  },
  button:{
    paddingVertical: 10, 
    backgroundColor: 'rgba(38,150,184, 0.6)', width: 150,
    borderRadius: 10,
  },
  buttonText:{
    color: 'white', alignSelf:'center'
  },
});
export default Account;
