import * as React from "react";
import { View, StyleSheet, Text, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import BottomView from "../components/BottomView";
import Topnav from "../components/Topnav";
import { Color, FontFamily, FontSize, Padding } from "../GlobalStyles";

const CreatePost = () => {
  const navigation = useNavigation();

  return (
    <View style={[styles.createPost, styles.iconLayout]}>
      <View style={[styles.rules, styles.rulesFlexBox]}>
        <View style={styles.rulesChild} />
        <View style={styles.strictlyAdhereToMmuCommuniParent}>
          <Text
            style={[styles.strictlyAdhereTo, styles.strictlyAdhereToTypo]}
          >{`Strictly adhere to MMU community guidelines and keep your  language
       in line with the school rules. `}</Text>
          <Text style={[styles.ensureYouMaintain, styles.strictlyAdhereToTypo]}>
            Ensure you maintain school conduct too in all conversations
          </Text>
        </View>
        <Text style={styles.campusVibes}>Campus vibes</Text>
      </View>
      <View style={styles.createPostInner}>
        <View>
          <Text style={[styles.topic, styles.topicTypo]}>Topic</Text>
          <Text style={[styles.composeYourMessage, styles.topicTypo1]}>
            Compose your message................
          </Text>
        </View>
      </View>
      <View style={[styles.iconPersonCircleParent, styles.rulesFlexBox]}>
        <Image
          style={styles.iconPersonCircle}
          resizeMode="cover"
          source={require("../assets/-icon-person-circle.png")}
        />
        <Text style={[styles.vibeInCampus, styles.topicTypo]}>
          Vibe in campus vibes
        </Text>
        <Pressable
          style={styles.iconsaxlinearclosesquare}
          onPress={() => navigation.goBack()}
        >
          <Image
            style={[styles.icon, styles.iconLayout]}
            resizeMode="cover"
            source={require("../assets/iconsaxlinearclosesquare.png")}
          />
        </Pressable>
      </View>
      <BottomView />
      <Topnav />
    </View>
  );
};

const styles = StyleSheet.create({
  iconLayout: {
    overflow: "hidden",
    width: "100%",
  },
  rulesFlexBox: {
    alignItems: "center",
    position: "absolute",
  },
  strictlyAdhereToTypo: {
    color: Color.gray_400,
    textAlign: "left",
    fontFamily: FontFamily.interRegular,
    fontSize: FontSize.size_3xs,
    left: 0,
    position: "absolute",
  },
  topicTypo: {
    fontFamily: FontFamily.interSemibold,
    fontWeight: "600",
    textAlign: "left",
  },
  topicTypo1: {
    fontSize: FontSize.size_xs,
    color: Color.gray_400,
  },
  rulesChild: {
    backgroundColor: "rgba(0, 0, 0, 0.3)",
    width: 393,
    height: 1,
  },
  strictlyAdhereTo: {
    top: 0,
    textAlign: "left",
  },
  ensureYouMaintain: {
    top: 26,
    textAlign: "left",
  },
  strictlyAdhereToMmuCommuniParent: {
    width: 350,
    height: 38,
    marginTop: 20,
  },
  campusVibes: {
    color: Color.black,
    textAlign: "left",
    fontFamily: FontFamily.interRegular,
    fontSize: FontSize.size_3xs,
    marginTop: 20,
  },
  rules: {
    top: 679,
    left: 0,
    alignItems: "center",
  },
  topic: {
    fontSize: FontSize.size_xs,
    color: Color.gray_400,
  },
  composeYourMessage: {
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    marginTop: 13,
    textAlign: "left",
  },
  createPostInner: {
    top: 160,
    left: 10,
    padding: Padding.p_3xs,
    position: "absolute",
  },
  iconPersonCircle: {
    width: 20,
    height: 20,
  },
  vibeInCampus: {
    fontSize: FontSize.size_sm,
    marginLeft: 81,
    color: Color.black,
  },
  icon: {
    height: "100%",
  },
  iconsaxlinearclosesquare: {
    width: 25,
    height: 25,
    marginLeft: 81,
  },
  iconPersonCircleParent: {
    top: 110,
    left: 20,
    flexDirection: "row",
  },
  createPost: {
    backgroundColor: Color.whitesmoke_100,
    flex: 1,
    height: 852,
  },
});

export default CreatePost;
