        width: 25, height: 25,
        borderWidth: 2, borderColor: 'white', borderRadius: 100,
        left: -10,
    },
    classmate3: {
        width: 25, height: 25,
        borderWidth: 2, borderColor: 'white', borderRadius: 100,
        left: -20,
    },
    classmate4: {
        width: 25, height: 25,
        borderWidth: 2, borderColor: 'white', borderRadius: 100,
        left: -30,
    },
    confirmPresent:{
        position: 'absolute',
        right: 20, bottom: 10,
         borderRadius: 10,
         backgroundColor:'white',
        paddingHorizontal: 10, paddingVertical: 3,
    }, 
    plus:{
