import React from 'react';
import { StyleSheet, View, Text, SafeAreaView, FlatList } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import Topnav from '../components/Topnav';

const messages = [
  {
    id: 1,
    from: 'John Doe',
    subject: 'Meeting Tomorrow',
    body: 'Hey, just a reminder that we have a meeting tomorrow at 2 PM. See you then!',
  },
  {
    id: 2,
    from: 'Jane Smith',
    subject: 'Project Status Update',
    body: 'Hi, I just wanted to give you a quick update on the project. We are ahead of schedule and should be finished by the end of the month.',
  },
  {
    id: 3,
    from: 'Mark Johnson',
    subject: 'New Product Release',
    body: 'We just released a new product that I think you will be interested in. Check it out on our website.',
  },
];

const Inbox = () => {
  const renderMessageItem = ({ item }) => (
    <View style={styles.messageItem}>
      <View style={styles.messageIconContainer}>
        <FontAwesome name="envelope" size={24} color="#007aff" />
      </View>
      <View style={styles.messageContent}>
        <Text style={styles.messageFrom}>{item.from}</Text>
        <Text style={styles.messageSubject}>{item.subject}</Text>
        <Text style={styles.messageBody}>{item.body}</Text>
      </View>
    </View>
  );

  return (
    <SafeAreaView style={styles.container}>
      <Topnav/>
      <FlatList
        data={messages}
        renderItem={renderMessageItem}
        keyExtractor={item => item.id.toString()}
        contentContainerStyle={styles.messagesContainer}
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  messagesContainer: {
    paddingHorizontal: 16,
    paddingBottom: 16,
  },
  messageItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  messageIconContainer: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#f0f0f0',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 16,
  },
  messageContent: {
    flex: 1,
  },
  messageFrom: {
    fontWeight: 'bold',
    fontSize: 16,
    marginBottom: 4,
  },
  messageSubject: {
    fontSize: 14,
    color: '#666',
    marginBottom: 4,
  },
  messageBody: {
    fontSize: 14,
    color: '#666',
  },
});

export default Inbox;
