import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet,ActivityIndicator } from 'react-native';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';
import { db, app} from '../firebase/firebase.js';
import AttachmentButton from '../components/AttachmentButton.js';
import PostTop from '../components/PostTop.js';
import { useSelector } from 'react-redux';
import { Picker } from "@react-native-picker/picker";
import { FontAwesome5 } from '@expo/vector-icons';
import Courses from "../components/courses";
import UserTaggingTextInput from '../components/TagModal.js';
import { useNavigation } from "@react-navigation/native";
import { getStorage, ref, uploadBytes, getDownloadURL} from 'firebase/storage';
import { debounce } from 'lodash';


export default function CreatePost() {
  const navigation = useNavigation();
  const [topic, setTopic] = useState('');
  const [message, setMessage] = useState('');
  const [imageTag, setImageTag] = useState('');
  const [topicTag, setTopicTag] = useState('fire');
  const [imageUri, setImageUri] = useState(null);
  const [audience, setAudience] = useState('all');
  const [tagList, setTagList] = useState([]);
  const [textInputKey, setTextInputKey] = useState(0);
  const handleTagListChange = (tags) => {
    setTagList(tags);
  };


  const handleMessageChange =(text) => {
    setMessage(text);
  };



// Initialize all the userprofile data required for the post
  const defaultProfileImage = require("../assets/graduate.png");
  const defaultField = "Guest";
  const [field, setField] = useState(defaultField);
  const [profileImage, setProfileImage] = useState(defaultProfileImage);
  const userProfile = useSelector((state) => state.userProfile);
  const campus = useSelector(state => state.campus);
  const courseCode = `${campus.faculty}${campus.course}`
  const courseName = Courses[courseCode];
  const userId = userProfile.userId;
  const vibe = campus.id;

  useEffect(() => {
    setField(courseName);
    setProfileImage(
      userProfile.profileImage ? { uri: userProfile.profileImage } : defaultProfileImage
    );
  }, [userProfile]);


  const [selectedColor, setSelectedColor] = useState('orange');
  const icons = [ 
       { icon: 'fire', label: 'Hot' },  { icon: 'search-dollar', label: 'money' },  
       { icon: 'heartbeat', label: 'love' }, 
       { icon: 'book-open', label: 'Education' },  { icon: 'yin-yang', label: 'Life' }, 
        { icon: 'praying-hands', label: 'Prayer' }, 
       { icon: 'hand-holding-heart', label: 'Share Love' }, 

      ];


//  Handling image passed from attachment button
      const handleImageCapture = (uri) => {
        setImageUri(uri);
      };
      const handleDeleteImage = () => {
        setImageUri(null);
      };

      // Handle posting
      const[isLoading, setLoading] = useState(false);
      const handlePost = async () => {
        setLoading(true);
        if (!topic || !topicTag || !audience ) {
          alert('Please enter a topic and message.');
          return;
        }
        const handleImageUpload = async (imageUri) => {
          const storage = getStorage(app);
          const storageRef = ref(storage, `images/${Date.now()}_${imageUri}`);

          // Fetch image data
          const response = await fetch(imageUri);
          const blob = await response.blob();

          // Upload image data to Firebase Storage
          const uploadTask = uploadBytes(storageRef, blob);
          await uploadTask;
          // Get the download URL for the uploaded image
          const downloadURL = await getDownloadURL(storageRef);
          return downloadURL;
        };

        let downloadURL = '';
        if (imageUri){
        downloadURL = await handleImageUpload(imageUri);
        }

        try {
          // Create a new post document in the database
          const postRef = await addDoc(collection(db, 'Posts'), {
            userId, //user profile
            audience, vibe, //who to view
            topicTag, topic, message,  //main post
            imageTag, downloadURL, tagList,//Optional attachment
            createdAt: serverTimestamp(),  //timestamp
            likes: [], dislikes: [], comments: {}, pinned: false,
          });
          setTopic('');
          setMessage('');
          setTagList('');
          setTextInputKey(textInputKey + 1);
          setImageUri(null); 

           console.log('Post created successfully')
           setLoading(false);
           navigation.navigate('BottomTabsRoot', { screen: 'Feed' });

        } catch (error) {
          console.error('Error posting:', error);
          alert('Error posting message. Please try again later.');
        }  
      };  


  return (
    <View style={styles.container}>
      <PostTop />
      <View style={styles.inputContainer}>
         <View style={styles.creatorview}>
           <Image source={profileImage} style={styles.image} />
           <View style={styles.whoview}>
             <Picker selectedValue={audience} onValueChange={(value) => setAudience(value)}style={styles.picker} >
                <Picker.Item label="Everyone" value="all" />
                <Picker.Item label={campus.campus} value={campus.id} />
                <Picker.Item label={campus.faculty} value={campus.faculty} />
              </Picker>
           </View>
        </View>
        <View style={styles.bubble}>
           <View style={styles.topicIcon}>
             <View style={styles.categoryPickerContainer}>
               <Picker selectedValue={topicTag}onValueChange={(value) => setTopicTag(value)} style={styles.categoryPicker} mode='dropdown'>
                {icons.map((icon, index) => (
               <Picker.Item key={index}label={icon.label}value={icon.icon} color={icon.color} />
                 ))}
               </Picker>
               <FontAwesome5 name={topicTag} size={25} color={selectedColor}  style={styles.categoryPickerIcon}/>
              </View>

              <TextInput
               style={styles.topicInput}
               placeholder="Enter topic"
               onChangeText={setTopic}
               value={topic}
               maxLength={60}
             />
           </View>

           <View style={styles.holder}> 
              <UserTaggingTextInput onTagListChange={handleTagListChange} onMessageChange={handleMessageChange} id={textInputKey} />   
              <View style={styles.imageUri}>
                {imageUri ? (
                <>
               <View style={styles.display}>
                 <Image source={{ uri: imageUri }} style={styles.attached} />
                 <TouchableOpacity onPress={handleDeleteImage} style={styles.deleteButton}>
                   <FontAwesome5 name="times" size={20} color="#2696b8" />
                 </TouchableOpacity>
               </View>
               <TextInput
                 style={styles.tag}
                 placeholder='Tag your image'
                 onChangeText={setImageTag}
                 value={imageTag}
                 maxLength={20}
               />
             </>
             ) : null}
             </View>
           </View>
      </View>

      {isLoading && (
        <View style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <ActivityIndicator />
        </View>
      )}
     </View>
     <View style={styles.bottom}>
     <AttachmentButton 
        onImageCapture={handleImageCapture} 
        onDeleteImage={handleDeleteImage} 
     />

        <TouchableOpacity style={styles.postButton} onPress={handlePost}>
           <Text style={styles.postButtonText}>Post</Text>
         </TouchableOpacity>
      </View>
  </View>
 )};



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor:'#f0f0f0',
    alignItems: 'center',
    // justifyContent: '',
    },
    inputContainer: {
      width: '100%', marginTop: 2
    },
    bubble:{
      backgroundColor: 'white', borderRadius: 5,
      width: '100%', alignSelf: 'center', height: '100%',
      marginTop: 2,
    },
    creatorview:{
      flexDirection: 'row', padding: 20,
      backgroundColor: 'white'
    },
    whoview:{
     borderWidth: 1, borderColor: '#2696b8', 
      width: 200, marginLeft: 10, borderRadius: 5, height: 50,
     justifyContent: 'center',

    },
    picker:{
      color: '#2696b8',
     },

    image: {
      width: 50, height: 50, borderRadius: 100,padding: 20
    },
    topicIcon:{
      padding: 10, 
      borderRadius: 5,
     flexDirection: 'row',
    },
    categoryPickerContainer:{
      width: '10%', marginLeft: 5,
    },
    topicInput: {
    width: '80%', flex: 1,
    height: 50,
    textAlignVertical: 'top', padding: 15, paddingLeft: 1,
    borderBottomColor: '#f0f0f0', borderBottomWidth: 1,
    },
    attachmentContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 10,
    },
    attachmentButton: {
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    },
    attachmentIcon: {
    width: 40,
    height: 40,
    marginBottom: 5,
    },
    attachmentText: {
    fontWeight: 'bold',
    },
    postButton: {
    backgroundColor: '#2C96B8',
    borderRadius: 10, right: 30,position: 'absolute',
    justifyContent: 'center',
    width: 60, height: 35,
    alignSelf:'center', alignItems: 'center',
    },
    postButtonText: {
    color: '#fff',
    fontWeight: 'bold', alignSelf: 'center',
    },

    categoryPickerIcon: {
      position: 'absolute',
      top: 10, 
      right: 10, backgroundColor:'#ffff', borderRadius: 100,
    },

    bottom:{
      borderTopColor: '#f0f0f0', borderTopWidth: 2,
      bottom: 0, backgroundColor: 'white',
      flexDirection: 'row',
       position: 'absolute',
        height: 60, padding: 10,
