import React, { useState, useEffect } from "react";
import { View, Text, TextInput, Button, StyleSheet, TouchableOpacity, Image, StatusBar } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { getFirestore, doc, setDoc } from "firebase/firestore";
import { auth } from "../firebase/firebase";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { Picker } from "@react-native-picker/picker";
import * as ImagePicker from "expo-image-picker";
import defaultProfileImage from "../assets/graduate.png";


const EditProfile = ({ navigation }) => {
  const [username, setUsername] = useState("");
  const [field, setField] = useState("");
  const [profileImage, setProfileImage] = useState(null);
  const [profileImageUrl, setProfileImageUrl] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [uid, setUid] = useState("");
  const [userProfileData, setUserProfileData] = useState(null);
  console.log(profileImage);

  useEffect(() => {
    const getData = async () => {
      try {
        const userProfileData = await AsyncStorage.multiGet([
          "userId",
          "username",
          "profileImage",
          "field",
        ]);
        const userId = userProfileData[0][1];
        const username = userProfileData[1][1];
        const profileImage = userProfileData[2][1];
        const field = userProfileData[3][1];
        setProfileImage(profileImage ? { uri: profileImage } : defaultProfileImage);
        setUsername(username || defaultUsername);
        setField(field || defaultField);
        setUserProfileData(userProfileData); // update userProfileData
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);
  
  // handleSaveProfile function to update user profile data in firestore
  const handleSaveProfile = async () => {
    try {
      if (field === "") {
        alert("Please select a new field of study");
        return;
      }
      if (username === "") {
        alert("Please add a new username");
        return;
      }
      setLoading(true);
      const db = getFirestore();
      const userProfileDoc = doc(db, "userProfiles", uid);

      await setDoc(userProfileDoc, {
        username: username,
        field: field,
        profileImageUrl: profileImageUrl,
      });

      navigation.navigate("BottomTabsRoot", { screen: "Feed" });
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };
   // Function to handle selecting a profile image
   const handleSelectProfileImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== "granted") {
      alert("Sorry, we need camera roll permissions to make this work!");
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 4],
      quality: 1,
    });

    if (!result.cancelled) {
      setProfileImageUrl(result.uri);
      setProfileImage(result);
    }
  };

  return (
    <View style={styles.container}>
      {/* <Topnav/> */}
      <StatusBar backgroundColor="#f5f5f5"/>
      <TouchableOpacity style={styles.profileImageContainer} onPress={handleSelectProfileImage}>
        {profileImage ? (
            <Image source={profileImage} style={styles.profile}/>
            ) : (
                <Image source={defaultProfileImage} style={styles.profile}/>
                )}
        <MaterialCommunityIcons
           name="camera-plus"
            size={32}
            color="white"
           style={styles.cameraIcon}
          />
      </TouchableOpacity>

      <View style={styles.form}>
        <View style={styles.formGroup}>
          <Text style={styles.label}>Username</Text>
          <TextInput
            style={styles.input}
            onChangeText={(text) => setUsername(text)}
            value={username}
            placeholder={username}
          />
       </View>
        <View style={styles.formGroup}>
          <Text style={styles.label}>Field of Study</Text>
          <Picker
            selectedValue={field}
            onValueChange={(value) => setField(value)}
            style={styles.picker}
           >
            <Picker.Item label="Select a field of study..." value="" />
            <Picker.Item label="Computer Science" value="Computer Science" />
            <Picker.Item label="Mathematics" value="Mathematics" />
            <Picker.Item label="Physics" value="Physics" />
            <Picker.Item label="Engineering" value="Engineering" />
            <Picker.Item label="Medicine" value="Medicine" />
          </Picker>
        </View>
        <TouchableOpacity style={styles.button} onPress={handleSaveProfile}>
            <Text style={styles.buttonText}>Save Profile</Text>
        </TouchableOpacity>
       
      </View>
      {isLoading && (
        <View style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <ActivityIndicator />
        </View>
      )}
    </View>
   );

};
const styles = StyleSheet.create({
    container: {
       flex: 1,
        backgroundColor: "#F5F5F5",
        alignItems: "center",
        justifyContent: "center",
      },
            profileImageContainer: {
            position: "relative",
            alignSelf: "center",
            marginBottom: 20,
            },
            profile: {
            width: 150,
            height: 150,
            borderRadius: 75,
            },
            cameraIcon: {
            position: "absolute",
            bottom: 0,
            right: 0,
            backgroundColor: '#2C96B8',
            borderRadius: 20,
            padding: 5,
            },
            form: {
            width: "80%",
            },
            formGroup: {
            marginBottom: 20,
            },
            label: {
            fontSize: 16,
            marginBottom: 5,
            color: "#444444",
            },
            input: {
            borderWidth: 1,
            borderColor: "#DDDDDD",
            borderRadius: 5,
            padding: 10,
            fontSize: 16,
            color: "#555555",
            },
            picker: {
            borderWidth: 1,
            borderColor: "#DDDDDD",
            borderRadius: 5,
            fontSize: 16,
            color: "#555555",
            },
            button: {
            backgroundColor: "#00BFFF",
            borderRadius: 5,
            padding: 10,
            alignItems: "center",
            marginTop: 20,
            },
            buttonText: {
            color: "white",
            fontSize: 16,
            fontWeight: "bold",
            },
 });

export default EditProfile;
