import React, { useState } from 'react';
import { View, StyleSheet, TouchableOpacity, Image, Text } from 'react-native';
import * as DocumentPicker from 'expo-document-picker';
import * as ImagePicker from 'expo-image-picker';
import { useDispatch } from 'react-redux';
import { setCapturedImage } from '../src/store/store';

const AttachmentButton = (props) => {
  const [image, setImage] = useState(null);
  const dispatch = useDispatch();

  const selectImage = async () => {
    let permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert('Permission to access camera roll is required!');
      return;
    }

    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImage(result);
      dispatch(setCapturedImage(result));
    }
  };

  const selectDocument = async () => {
    let result = await DocumentPicker.getDocumentAsync({});

    if (!result.cancelled) {
      props.setDocument(result.uri);
    }
  };

  const selectPoll = () => {
    // Implement functionality for poll attachment
  };

  return (
    <View style={styles.body}>
         {image && (
        <View style={styles.imageContainer}>
          <Image source={{ uri: image.uri }} style={styles.image} />
        </View>
      )}
      <View style={styles.attachmentContainer}>
        <TouchableOpacity onPress={selectImage}>
            <Image source={require('../assets/image.png')} style={styles.attachmentIcon} />
        </TouchableOpacity>
        <TouchableOpacity onPress={selectDocument}>
            <Image source={require('../assets/speech-bubble.png')} style={styles.attachmentIcon} />
        </TouchableOpacity>
        <TouchableOpacity onPress={selectPoll}>
            <Image source={require('../assets/poll.png')} style={styles.attachmentIcon} />
        </TouchableOpacity>
      </View>
     
    </View>
  );
};



const styles = StyleSheet.create({
  attachmentContainer: {
    flexDirection: 'row',
    // justifyContent: 'space-between',
    left: 10, alignItems: 'center',
  },
  body:{
    position: 'absolute',
    backgroundColor: 'white',
    padding: 15, width: 393,
  },

  attachmentIcon: {
    height: 24,
    width: 24,
    marginLeft: 10,
    marginRight:20,
  },
  imageContainer: {
    // position: 'relative',
  },
  image: {
    width: '100%',
    height: 200,
  },
});

export default AttachmentButton;
