import React, { useState } from 'react';
import { View, TextInput, Button, Text, StyleSheet, Modal } from 'react-native';

const Search = () => {
  const [searchText, setSearchText] = useState('');
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);

  const handleSearch = () => {
    // Simulate fetching search results from a server
    const data = [
      { id: 1, name: 'car', description: 'A four-wheeled vehicle' },
      { id: 2, name: 'bus', description: 'A vehicle for transporting people' },
      { id: 3, name: 'Daysterious', description: 'A mysterious word' },
    ];

    // Filter the results based on the search text
    const filteredResults = data.filter((result) =>
      result.name.toLowerCase().includes(searchText.toLowerCase())
    );

    setResults(filteredResults);
    setShowResults(true);
  };

  const handleModalClose = () => {
    setShowResults(false);
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Search"
        value={searchText}
        onChangeText={setSearchText}
      />
      <Button title="Search" onPress={handleSearch} style={styles.searchbtn}/>
      
      <Modal visible={showResults} animationType="slide">
        <View style={styles.modal}>
          <Button title="Close" onPress={handleModalClose} />
          {results.map((result) => (
            <View key={result.name} style={styles.result}>
              <Text style={styles.resultName}>{result.name}</Text>
              <Text style={styles.resultDescription}>
                {result.description}
              </Text>
            </View>
          ))}
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
    input:{
        backgroundColor: 'white',
        flex: 1,
        height: 50,
        marginRight: 10
    },
    container:{
        flexDirection: 'row',
        alignItems: 'center',
        marginEnd: 10,
        marginBottom: 2,
    },
    searchbtn:{
        flex: 0,
        width: 80,
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
    },
    modal: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    },
    result: {
        margin: 10
    },
    resultName: {
        fontWeight: 'bold',
        fontSize: 20
    },
    resultDescription: {
        fontSize: 16,
        color: 'gray'
    }
});

export default Search;
