import React, { useState } from 'react';
import { View, TextInput, Image, TouchableOpacity, Button, StyleSheet } from 'react-native';

const Catalogue = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    image: '',
    name: '',
    price: '',
    description: ''
  });

  const handleAddProduct = () => {
    setProducts([...products, newProduct]);
    setNewProduct({ image: '', name: '', price: '', description: '' });
  };

  const handleUpdateProduct = (field, value) => {
    setNewProduct({ ...newProduct, [field]: value });
  };

  return (
    <View>
      {products.map((product, index) => (
        <View key={index}>
          <Image source={{ uri: product.image }} style={{ width: 100, height: 100 }} />
          <TextInput
            value={product.name}
            onChangeText={(text) => handleUpdateProduct('name', text)}
          />
          <TextInput
            value={product.price}
            onChangeText={(text) => handleUpdateProduct('price', text)}
          />
          <TextInput
            value={product.description}
            onChangeText={(text) => handleUpdateProduct('description', text)}
          />
        </View>
      ))}

      <View style={styles.card}>
        <TextInput
          placeholder="Image URL"
          value={newProduct.image}
          onChangeText={(text) => handleUpdateProduct('image', text)}
        />
        <TouchableOpacity style={styles.setImage}>

        </TouchableOpacity>
        <TextInput
          placeholder="Product Name"
          value={newProduct.name}
          onChangeText={(text) => handleUpdateProduct('name', text)}
        />
        <TextInput
          placeholder="Product Price"
          value={newProduct.price}
          onChangeText={(text) => handleUpdateProduct('price', text)}
        />
        <TextInput
          placeholder="Product Description"
          value={newProduct.description}
          onChangeText={(text) => handleUpdateProduct('description', text)}
        />
        <Button title="Add Product" onPress={handleAddProduct} />
      </View>
    </View>
  );
};


const styles = StyleSheet.create({
    card:{
        // borderWidth:1 ,
        padding: 10, 
        borderRadius: 5,
        //  borderColor: '#f0f0f0'
        elevation: 5,
        backgroundColor: '#ffff'
    }
})
export default Catalogue;
