import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { useSelector } from 'react-redux';
import Topnav from '../components/Topnav';

const Account = () => {
  const userProfile = useSelector(state => state.userProfile);

  const defaultProfileImage = require('../assets/graduate.png');
  const defaultUsername = "Student";
  const defaultField = "Guest";

  const profileImage = userProfile.profileImage ? { uri: userProfile.profileImage } : defaultProfileImage;
  const username = userProfile.username || defaultUsername;
  const field = userProfile.field || defaultField;


  console.log("profileImage:", profileImage);


  return (
    <View style={styles.container}>
      <Topnav/>
      <View style={styles.body}>
         <View style={styles.profilepic}>
            <Image source={profileImage} style={styles.image} resizeMode='cover'/>
         </View>
         <View style={styles.profiletext}>
            <Text style={styles.username}>{username}</Text>
            <Text style={styles.field}>{field}</Text>
         </View>
       </View>
    </View>
  );
};


const styles = StyleSheet.create({
  body:{
    top: 70,
    flexDirection: 'row', 
  },
  profilepic:{
    left: 20,
  },
  image:{
    width:80, height: 80,
    borderRadius: 100,
  },
  profiletext:{
    marginLeft: 30,
    flexDirection: 'column',
  },
  username:{
    fontSize: 17, fontWeight: 600,
    marginVertical: 10,
  },
});

export default Account;
