import React, { useState } from "react";
import { View, Text, StyleSheet, Image, StatusBar } from "react-native";
import { TextInput as RNPTextInput } from "react-native-paper";
import { createMaterialTopTabNavigator } from "@react-navigation/material-top-tabs";
import Scrollview1 from "../components/Scrollview";
import Messages from "../components/Messages";
import Chats from "../components/Chats";
import { Color, FontSize, Border, Padding } from "../GlobalStyles";
import Topnav from "../components/Topnav";

const TopTab = createMaterialTopTabNavigator();

const Inbox = () => {
  const [searchTextInput, setSearchTextInput] = useState("text");

  return (
    <View style={styles.inbox}>
      <Topnav/>
      <View style={styles.body}>
      <TopTab.Navigator style={styles.messagebarToptabs}>
        <TopTab.Screen
          name="Scrollview"
          component={Scrollview1}
          options={{
            tabBarLabel: "Messages",
            tabBarIcon: () => <Messages />,
          }}
        />
      </TopTab.Navigator>
      <View style={styles.header}>
        <Chats /><Chats />
      </View>
      <View style={styles.body2}>
      <RNPTextInput
        style={styles.search}
        placeholder="Search Direct Messages"
        value={searchTextInput}
        onChangeText={setSearchTextInput}
        placeholderTextColor="#8a8a8a"
        theme={{ colors: { text: "#8a8a8a", background: "#fff" } }}
      />
      </View>

      <View style={[styles.bottomview, styles.header1Position]}>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  topTabContent: {
    flex: 1,
  },
  messagebarToptabs: {
    width: "100%",
  },
  bcs32Typo: {
    textAlign: "left",
    color: Color.black,
    fontSize: FontSize.size_xs,
    left: 1,
    position: "absolute",
  },
  nowTypo: {
    textAlign: "center",
    lineHeight: 11,
    letterSpacing: -0.2,
    fontSize: FontSize.size_2xs,
    position: "absolute",
  },
  wrapperLayout: {
    height: 20,
    width: 20,
  },
  header1Position: {
    left: 0,
    position: "absolute",
  },
  topnavBg: {
    backgroundColor: Color.white,
    flexDirection: "row",
  },
  iconsaxlinearprofile2userSpaceBlock: {
    overflow: "hidden",
    marginLeft: 53.5,
  },
  text: {
    display: "none",
    marginLeft: 40,
  },
  header: {
    top: 100,
    left: 87,
    fontWeight: '700',
    justifyContent: 'space-between',
    flexDirection: "row",
    position: "absolute",
  },
  search: {
    top: 200,

    left: 18,
    borderRadius: Border.br_3xs,
    height: 40,
    paddingHorizontal: 66,
    paddingVertical: Padding.p_3xs,
    backgroundColor: Color.white,
    alignItems: "center",
    flexDirection: "row",
    position: "absolute",
  },
  groupIcon: {
    height: 22,
    width: 20,
  },
  iconsaxlinearprofile2user: {
    marginLeft: 53.5,
    height: 20,
    width: 20,
  },
  iconsaxlinearmonitormobbile: {
    width: 24,
    height: 24,
    marginLeft: 53.5,
  },
  whatsappImage20230122At5Icon: {
    width: 31,
    height: 31,
    marginLeft: 53.5,
    borderRadius: Border.br_81xl,
  },
  bottomnav: {
    paddingHorizontal: Padding.p_12xl,
    paddingVertical: Padding.p_mini,
    height: 60,
    alignItems: "center",
  },
  bottomview: {
    top: 792,
  },
  header1: {
    top: 0,
  },
  inbox: {
    backgroundColor: Color.whitesmoke_100,
    flex: 1,
    width: "100%",
    height: 852,
  },
});

export default Inbox;
