import * as React from "react";
import {
  View,
  StyleProp,
  ViewStyle,
  Image,
  StyleSheet,
  Text,
} from "react-native";
import { Border, FontSize, FontFamily, Color, Padding } from "../GlobalStyles";

const Reply = ({ style }) => {
  return (
    <View>
      <View style={styles.userMessage}>
        <Image
          style={styles.river1Icon}
          resizeMode="cover"
          source={require("../assets/river-1.png")}
        />
        <View style={styles.message}>
          <Text style={styles.minAgo}>2min ago</Text>
          <View style={styles.content}>
            <Text style={styles.tophaz}>Tophaz</Text>
            <Text style={styles.lastEventWas}>
              Last event was dope! I definitely cant afford to miss this one.
              Tupatane #siron
            </Text>
          </View>
        </View>
      </View>
      <View style={styles.buttons}>
        <View style={styles.buttonsInner}>
          <View style={styles.replyParent}>
            <Text style={styles.reply1}>Reply</Text>
            <Text style={styles.share}>Share</Text>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  river1Icon: {
    position: "relative",
    borderRadius: Border.br_81xl,
    width: 45,
    height: 45,
  },
  minAgo: {
    position: "relative",
    fontSize: FontSize.size_3xs,
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    color: Color.gray_400,
    textAlign: "left",
  },
  tophaz: {
    position: "relative",
    fontSize: FontSize.size_xs,
    fontWeight: "600",
    fontFamily: FontFamily.interSemibold,
    color: Color.black,
    textAlign: "left",
  },
  lastEventWas: {
    position: "relative",
    fontSize: FontSize.size_xs,
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    color: Color.black,
    textAlign: "left",
    width: 297,
    marginTop: 5,
  },
  content: {
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    marginTop: -10,
  },
  message: {
    flexDirection: "column",
    alignItems: "flex-end",
    justifyContent: "flex-start",
    marginLeft: 6,
  },
  userMessage: {
    flexDirection: "row",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    marginTop: 10,
  },
  reply1: {
    position: "relative",
    fontSize: FontSize.size_3xs,
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    color: Color.dimgray,
    textAlign: "left",
  },
  share: {
    position: "relative",
    fontSize: FontSize.size_3xs,
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    color: Color.dimgray,
    textAlign: "left",
    marginLeft: 50,
  },
  replyParent: {
    flexDirection: "row",
    alignItems: "flex-start",
    justifyContent: "flex-start",
  },
  buttonsInner: {
    flexDirection: "column",
    padding: Padding.p_3xs,
    alignItems: "flex-start",
    justifyContent: "flex-start",
  },
  buttons: {
    flexDirection: "column",
    paddingHorizontal: Padding.p_32xl,
    paddingVertical: 0,
    alignItems: "flex-start",
    justifyContent: "flex-start",
    marginTop: 5,
  },
});

export default Reply;
