import * as React from "react";
import { View, Text, StyleSheet, Image, Pressable } from "react-native";
import { TextInput as RNPTextInput } from "react-native-paper";
import Topnav from "../components/Topnav";
import { Color, FontFamily, FontSize, Padding, Border } from "../GlobalStyles";

const CreateClub = () => {
  return (
    <View style={styles.createClub}>
      <View style={styles.strictlyAdhereToMmuCommuniParent}>
        <Text
          style={[styles.strictlyAdhereTo, styles.clubNameFlexBox]}
        >{`Strictly adhere to MMU community guidelines and keep your  language
       in line with the school rules. `}</Text>
        <Text style={[styles.ensureYouMaintain, styles.clubNameFlexBox]}>
          Ensure you maintain school conduct too in all conversations
        </Text>
      </View>
      <View style={styles.campusVibesWrapper}>
        <Text style={[styles.campusVibes, styles.campusVibesTypo]}>
          Campus vibes
        </Text>
      </View>
      <View style={[styles.clubNameParent, styles.parentPosition]}>
        <Text style={[styles.clubName, styles.clubNameFlexBox]}>Club name</Text>
        <Image
          style={styles.iconCheckCircle}
          resizeMode="cover"
          source={require("../assets/-icon-check-circle.png")}
        />
      </View>
      <View style={[styles.descriptionParent, styles.parentPosition]}>
        <RNPTextInput
          placeholder="Description"
          label="Label"
          mode="flat"
          placeholderTextColor="rgba(0, 0, 0, 0.5)"
          theme={{
            fonts: { regular: { fontFamily: "Inter", fontWeight: "Regular" } },
            colors: { text: "rgba(0, 0, 0, 0.5)" },
          }}
        />
        <View style={styles.iconInfoCircle}>
          <Image
            style={styles.vectorIcon}
            resizeMode="cover"
            source={require("../assets/vector3.png")}
          />
        </View>
      </View>
      <Pressable style={styles.submitClubRequestWrapper} onPress={() => {}}>
        <Text style={[styles.submitClubRequest, styles.campusVibesTypo]}>
          Submit Club Request
        </Text>
      </Pressable>
      <Topnav />
    </View>
  );
};

const styles = StyleSheet.create({
  clubNameFlexBox: {
    textAlign: "left",
    color: Color.gray_400,
  },
  campusVibesTypo: {
    fontFamily: FontFamily.interMedium,
    fontWeight: "500",
    textAlign: "left",
  },
  parentPosition: {
    left: 23,
    position: "absolute",
  },
  strictlyAdhereTo: {
    fontFamily: FontFamily.interRegular,
    fontSize: FontSize.size_3xs,
    color: Color.gray_400,
  },
  ensureYouMaintain: {
    marginTop: 2,
    fontFamily: FontFamily.interRegular,
    fontSize: FontSize.size_3xs,
    color: Color.gray_400,
  },
  strictlyAdhereToMmuCommuniParent: {
    top: 752,
    left: 10,
    position: "absolute",
  },
  campusVibes: {
    fontSize: FontSize.size_5xs,
    color: Color.black,
  },
  campusVibesWrapper: {
    top: 804,
    left: 159,
    padding: Padding.p_3xs,
    flexDirection: "row",
    position: "absolute",
  },
  clubName: {
    fontSize: FontSize.size_xs,
    fontWeight: "600",
    fontFamily: FontFamily.interSemibold,
  },
  iconCheckCircle: {
    width: 20,
    height: 20,
    marginLeft: 242,
  },
  clubNameParent: {
    top: 117,
    alignItems: "center",
    flexDirection: "row",
  },
  vectorIcon: {
    width: 2,
    height: 6,
  },
  iconInfoCircle: {
    marginTop: 90,
    alignItems: "center",
  },
  descriptionParent: {
    top: 143,
    backgroundColor: Color.white,
    borderStyle: "solid",
    borderColor: "#c0c0c0",
    borderTopWidth: 1,
    width: 340,
    height: 141,
    paddingHorizontal: Padding.p_4xs,
    paddingVertical: Padding.p_6xs,
    overflow: "hidden",
  },
  submitClubRequest: {
    fontSize: FontSize.size_smi,
    color: Color.white,
  },
  submitClubRequestWrapper: {
    top: 299,
    left: 32,
    borderRadius: Border.br_3xs,
    backgroundColor: Color.steelblue_200,
    paddingHorizontal: 74,
    paddingVertical: Padding.p_4xs,
    flexDirection: "row",
    position: "absolute",
  },
  createClub: {
    backgroundColor: Color.whitesmoke_100,
    flex: 1,
    width: "100%",
    height: 852,
    overflow: "hidden",
  },
});

export default CreateClub;
