import React, { useState, useEffect } from 'react';
import { View, TextInput, Image, TouchableOpacity, Text, StyleSheet, ScrollView} from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import * as ImagePicker from "expo-image-picker";
import { db } from '../firebase/firebase';
import { doc, getDoc } from 'firebase/firestore';
import { useSelector } from 'react-redux';
const placeholderImage = 'https://via.placeholder.com/1000';
const userId = useSelector((state => state.userProfile.userId))

const ProductEditing = ({ product, handleUpdateProduct, handleSaveProduct, handleCancelProduct, handleDeleteProduct }) => {
  const handleSelectImage = async() => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== "granted") {
      alert("Sorry, we need camera roll permissions to make this work!");
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 4],
      quality: 1,
    });

    if (!result.canceled) {
      handleUpdateProduct('image', result.uri);
    }
  };


  return (
    <View style={styles.holder}>
      <TouchableOpacity onPress={handleSelectImage} style={styles.select}>
        <Image
          source={product.image ? { uri: product.image } : { uri: placeholderImage }}
          style={{ width: '100%', height: 150, borderRadius: 5}}
        />
      </TouchableOpacity>
      <TextInput
      placeholder='Product name'
        style={styles.input}
        value={product.name}
        onChangeText={(text) => handleUpdateProduct('name', text)}
      />
      <TextInput
           placeholder='price'
           style={styles.input}
           value={product.price}
           onChangeText={(text) => handleUpdateProduct('price', text)}
           keyboardType="numeric"
       />

      <TextInput
        placeholder='Product description'
        style={styles.input}
        value={product.description}
        onChangeText={(text) => handleUpdateProduct('description', text)}
      />
      <View style={styles.buttonsContainer}>
        <TouchableOpacity style={styles.button} onPress={handleSaveProduct}>
          <Text style={styles.buttonText}>Save</Text>
        </TouchableOpacity>
        {product.name && (  
          <TouchableOpacity style={styles.button} onPress={handleDeleteProduct}>
            <Text style={styles.buttonText}>Delete</Text>
          </TouchableOpacity>
        )}
        <TouchableOpacity style={styles.button} onPress={handleCancelProduct}>
          <Text style={styles.buttonText}>Cancel</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const Catalogue = ({tuserId}) => {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productsRef = doc(db, 'userProfiles', userId, 'professional', 'business');
        const productsDoc = await getDoc(productsRef);

        if (productsDoc.exists()) {
          const productsData = productsDoc.data();
          const products = productsData.catalogue || [];
          setProducts(products);
        }
      } catch (error) {
        console.error('Error fetching products:', error);
        // Handle the error here, for example, by setting an error state or showing an error message.
      }
    };

    fetchProducts();
  }, []);

  const [newProduct, setNewProduct] = useState({
    id: null,
    image: '',
    name: '',
    price: '',
    description: ''
  });

  const [selectedProductIndex, setSelectedProductIndex] = useState(null);

  const handleUpdateProduct = (field, value) => {
    setNewProduct({ ...newProduct, [field]: value });
  };

  const handleSaveProduct = () => {
    if (!newProduct.name || !newProduct.description || !newProduct.price) {
      alert('Please fill in all fields');
    } else {
      if (selectedProductIndex !== null) {
        // Update existing product
        const updatedProducts = [...products];
        updatedProducts[selectedProductIndex] = newProduct;
        setProducts(updatedProducts);
        setNewProduct({
          id: null,
          image: '',
          name: '',
          price: '',
          description: '',
        });
      } else {
        // Add new product
        const id = Date.now().toString(); // Generate a unique id using Date.now()
        const newProductWithId = { ...newProduct, id };
        setProducts([...products, newProductWithId]);
        setNewProduct({
          id: null,
          image: '',
          name: '',
          price: '',
          description: '',
        });
      }

      setSelectedProductIndex(null);
    }
  };




  const handleCancelProduct = () => {
    setNewProduct({ id: null, image: '', name: '', price: '', description: '' });
    setSelectedProductIndex(null);
  };

  const handleDeleteProduct = () => {
    const updatedProducts = [...products];
    updatedProducts.splice(selectedProductIndex, 1);
    setProducts(updatedProducts);
    setSelectedProductIndex(null);
  };

  const handleEditProduct = (index) => {
    const selectedProduct = products[index];
    setNewProduct(selectedProduct);
    setSelectedProductIndex(index);
  };


  return (
    <View style={styles.container}>
      {selectedProductIndex !== null ? (
        <ProductEditing
          product={newProduct}
          handleUpdateProduct={handleUpdateProduct}
          handleSaveProduct={handleSaveProduct}
          handleCancelProduct={handleCancelProduct}
          handleDeleteProduct={handleDeleteProduct}
        />
      ) : (
        <>
          {products.length === 0 && (
            <Text style={styles.crt}>Add products to your catalogue for easier access by your customers</Text>
          )}

          {products.map((product, index) => (
            <TouchableOpacity
              key={product.name}
              style={styles.card}
              onPress={() => handleEditProduct(index)}
            >
              <Image
                source={product.image ? { uri: product.image } : { uri: placeholderImage }}
                style={styles.image}
              />
              <View style={styles.cardText}>
                <Text style={styles.crt}>{product.name}</Text>
                <Text style={styles.crt}>{product.price}</Text>
                <Text style={styles.crt}>{product.description}</Text>
              </View>
            </TouchableOpacity>
          ))}

          <TouchableOpacity
            style={styles.card}
            onPress={() => setSelectedProductIndex(products.length)}
          >
            <FontAwesome name="edit" size={35} color="#707070" />
            <View style={styles.cardText}>
              <Text style={styles.crt}>Add new product</Text>
            </View>
          </TouchableOpacity>
        </>
      )}
    </View>
  );

};

const styles = StyleSheet.create({
  card: {
    flexDirection: 'row',
    borderWidth: 1,
    borderColor: '#e0e0e0',
    borderRadius: 5,
    padding: 10,
    alignItems: 'center',
    backgroundColor: '#fafafa',
    marginVertical: 10,
  },
  cardText: {
    marginLeft: 10
  },
  crt: {
    fontSize: 16
  },
  holder: {
    borderWidth: 1,borderColor: '#f0f0f0', borderRadius: 5,
    padding:5,
    paddingBottom: 30,
    backgroundColor: '#ffff',
  },
  select: {
    marginBottom: 10
  },
  input: {
    borderWidth: 1,
    borderColor: '#e0e0e0',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10
  },
  buttonsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between'
  },
  button: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: '#e0e0e0',
    borderRadius: 5,
    paddingVertical: 10,
    marginHorizontal: 5
  },
  buttonText: {
    fontSize: 16
  },
  image:{
    width: 100,
    height: 100,
    borderRadius: 5,
    borderWidth: 1
  },
  container:{
    // borderWidth: 1,
    marginBottom: 50,
  }
});

export default Catalogue;
