import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet, Picker } from 'react-native';
import { firebase } from '../firebase/firebase';

const Start = ({ navigation }) => {
  const [campuses, setCampuses] = useState([]);
  const [selectedCampus, setSelectedCampus] = useState('');

  useEffect(() => {
    const campusRef = firebase.firestore().collection('campuses');
    const unsubscribe = campusRef.onSnapshot((querySnapshot) => {
      const campuses = [];
      querySnapshot.forEach((doc) => {
        const { name } = doc.data();
        campuses.push({
          id: doc.id,
          name,
        });
      });
      setCampuses(campuses);
    });
    return unsubscribe;
  }, []);

  const handleCampusChange = (campus) => {
    setSelectedCampus(campus);
  };

  const handleContinue = () => {
    navigation.navigate('Landing', { campus: selectedCampus });
  };

  return (
    <View style={styles.container}>
      <View style={styles.logoContainer}>
        <Image source={require('../assets/graduate.png')} style={styles.logo} />
      </View>
      <View style={styles.selectCampusContainer}>
        <Text style={styles.selectCampusText}>Select Your College/Campus</Text>
        <View style={styles.selectCampusDropdown}>
          <Picker
            selectedValue={selectedCampus}
            onValueChange={handleCampusChange}
          >
            <Picker.Item label="Select campus" value="" />
            {campuses.map((campus) => (
              <Picker.Item key={campus.id} label={campus.name} value={campus.name} />
            ))}
          </Picker>
        </View>
      </View>
      <TouchableOpacity style={styles.continueButton} onPress={handleContinue}>
        <Text style={styles.continueButtonText}>Continue</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logoContainer: {
    marginBottom: 40,
  },
  logo: {
    width: 100,
    height: 100,
  },
  selectCampusContainer: {
    marginBottom: 40,
  },
  selectCampusText: {
    fontWeight: 'bold',
    marginBottom: 10,
  },
  selectCampusDropdown: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 20,
    padding: 10,
    width: 200,
    height: 60,
  },
  continueButton: {
    backgroundColor: '#4d4dff',
    borderRadius: 20,
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  continueButtonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default Start;
