import React, { useState } from "react";
import {
  View,
  FlatList,
  StyleSheet,
  Text,
  Image,
  StatusBar,
  Pressable,
} from "react-native";
import PostTop from "../components/PostTop";
import Destyevents1 from "../components/Destyevents1.js";
import Reply from "../components/Reply";
import { Padding, Color, FontSize, FontFamily } from "../GlobalStyles";
import { TextInput } from "react-native-paper";
import Tweet from "./Tweet";

const Post = () => {
  const [flatlistData, setFlatlistData] = useState([
    <Destyevents1/>,
    <Reply />,<Reply />,<Reply />,<Reply />,
  ]);

  return (
    <View style={styles.post}>
      <PostTop />
      <View style={[styles.scrollview, styles.scrollviewSpaceBlock]}>
        <FlatList
          style={styles.flatlist}
          data={flatlistData}
          renderItem={({ item }) => item}
          contentContainerStyle={styles.flatlistContent}
        />
      </View>
      <View style={styles.bottomview}>
          <Tweet/>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  flatlistContent: {
    flexDirection: "column",
    paddingHorizontal: 10,
    paddingVertical: 0,
  },
  scrollviewSpaceBlock: {
    padding: Padding.p_3xs,
    flexDirection: "row",
  },
  topnavBorder: {
    borderColor: "#f0f0f0",
    borderStyle: "solid",
    flexDirection: "row",
    backgroundColor: Color.white,
  },
  flatlist: {
    flex: 1,
  },
  scrollview: {
    top: 76,
    left: -6,
    position: "absolute",
  },
  joinTheConversation: {
    fontSize: FontSize.size_xs,
    fontWeight: "600",
    fontFamily: FontFamily.interSemibold,
    color: Color.dimgray,
    textAlign: "left",
  },
  vectorIcon: {
    width: 20,
    height: 21,
  },
  vectorWrapper: {
    marginLeft: 193,
  },
  joinconvo: {
    flexDirection:"row",
    height: 70,
    paddingHorizontal: 17,
    paddingVertical: 14,
    alignItems: "center",
  },
  bottomview: {
    top: 620,
    left: 0,
    marginLeft: 10,
    position: "absolute",

    borderTopColor: '#f0f0f0',
  },
  post: {
    width: "100%",
    height: 852,
    flex: 1,
    backgroundColor: Color.white,
  },
});

export default Post;
