import * as React from "react";
import {
  StyleProp,
  ViewStyle,
  View,
  Image,
  StyleSheet,
  Text,
} from "react-native";
import { Color, FontSize, FontFamily, Border, Padding } from "../GlobalStyles";

const Scrollview1 = ({ style }) => {
  return (
    <View style={[styles.scrollview, style, styles.topTabContent]}>
      <View style={styles.chat}>
        <Image
          style={styles.chatChild}
          resizeMode="contain"
          source={require("../assets/group-51.png")}
        />
        <View style={styles.bcs32Parent}>
          <Text style={[styles.bcs32, styles.bcs32Typo]}>BCS 3.2</Text>
          <Text
            style={[styles.youHaveA, styles.bcs32Typo]}
          >{`You have a PROLOG class at 11:00 AM.... `}</Text>
          <Text style={[styles.now, styles.nowTypo]}>now</Text>
          <View style={styles.wrapper}>
            <Text style={[styles.text, styles.nowTypo]}>2</Text>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  bcs32Typo: {
    textAlign: "left",
    color: Color.black,
    fontSize: FontSize.size_xs,
    left: 1,
    position: "absolute",
  },
  nowTypo: {
    textAlign: "center",
    lineHeight: 11,
    letterSpacing: -0.2,
    fontSize: FontSize.size_2xs,
    position: "absolute",
  },
  chatChild: {
    width: 60,
    height: 60,
  },
  bcs32: {
    top: 10,
    fontWeight: "600",
    fontFamily: FontFamily.interSemibold,
  },
  youHaveA: {
    top: 37,
    fontFamily: FontFamily.interRegular,
  },
  now: {
    top: 11,
    left: 265,
    fontWeight: "500",
    fontFamily: FontFamily.interMedium,
    color: Color.gray_100,
  },
  text: {
    top: 4,
    left: 7,
    fontWeight: "700",
    fontFamily: FontFamily.interBold,
    color: Color.white,
  },
  wrapper: {
    top: 32,
    left: 267,
    borderRadius: Border.br_81xl,
    backgroundColor: Color.dodgerblue,
    width: 20,
    height: 20,
    position: "absolute",
  },
  bcs32Parent: {
    borderStyle: "solid",
    borderColor: "#f0f0f0",
    borderBottomWidth: 1,
    width: 300,
    height: 70,
    marginLeft: 10,
  },
  chat: {
    flexDirection: "row",
  },
  scrollview: {
    alignSelf: "stretch",
    flex: 1,
    paddingHorizontal: Padding.p_2xs,
    paddingVertical: Padding.p_3xs,
  },
});

export default Scrollview1;
