import React, { useState } from 'react';
import { View, TextInput, Image, TouchableOpacity, Text, StyleSheet } from 'react-native';
import { Feather, Ionicons, FontAwesome} from '@expo/vector-icons';

const Catalogue = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    image: '',
    name: '',
    price: '',
    description: ''
  });

  const handleAddProduct = () => {
    setProducts([...products, newProduct]);
    setNewProduct({ image: '', name: '', price: '', description: '' });
  };

  const handleUpdateProduct = (field, value) => {
    setNewProduct({ ...newProduct, [field]: value });
  };

  return (
    <View>
      {products.map((product, index) => (
        <View key={index}>
          <Image source={{ uri: product.image }} style={{ width: 100, height: 100 }} />
          <TextInput
            value={product.name}
            onChangeText={(text) => handleUpdateProduct('name', text)}
          />
          <TextInput
            value={product.price}
            onChangeText={(text) => handleUpdateProduct('price', text)}
          />
          <TextInput
            value={product.description}
            onChangeText={(text) => handleUpdateProduct('description', text)}
          />
        </View>
      ))}

      {products.empty() && (
        <Text style={styles.crt}>Add products to your catalogue for easier access by your customers</Text>
      )}

       <TouchableOpacity style={styles.card}>
       <FontAwesome name="edit" size={35} color="#707070" />
        <View style={styles.cardText}>
           <Text style={styles.crt}>Add new product</Text>
        </View>
       </TouchableOpacity>
    </View>
  );
};


const styles = StyleSheet.create({
    card:{
        flexDirection: 'row',
        borderWidth: 1,borderColor: '#e0e0e0',borderRadius: 5,
        padding: 10, alignItems: 'center',
        backgroundColor :'#fafafa'
    },
    cardText: {
      marginLeft: 10
    }
})


export default Catalogue;
