import React, { useState, useEffect, useCallback } from "react";
import { View, StyleSheet, Pressable, Image, RefreshControl, VirtualizedList, BackHandler , StatusBar, ActivityIndicator, Dimensions} from "react-native";
import Topnav from "../components/Topnav";
import Snippet from "../components/snippet";
import { useNavigation } from "@react-navigation/native";
import Search from "../components/search.js";
import { db } from '../firebase/firebase'
import { getDocs, collection, orderBy, query, where, startAfter} from "firebase/firestore";
import CampusModal from "../components/filterTopButton";
import { useSelector } from 'react-redux';
import NetworkError from "../components/NetworkError";
import { Modal } from "react-native-paper";
import { useDispatch } from "react-redux";

const { width, height } = Dimensions.get('window');

const Feed = () => {
  const navigation = useNavigation();
  const [posts, setPosts] = useState([]);
  const active = useSelector((state) => state.filter.filter);
  const [errorMessage, setNetworkError] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  const [loading, setLoading] = useState(false)
  const dispatch = useDispatch();

  const fetchPosts = async () => {
    try {
      setLoading(true)
      const q = query(
        collection(db, 'Posts'),
        where('audience', '==', active),
        orderBy('likes', 'desc'),
        orderBy('createdAt', 'desc')
      );

      const snapshot = await getDocs(q);
      const postsData = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));

       if(postsData.length === 0){
        const alert =  `No posts in ${active} switching to All`;
        setNetworkError(alert);
        dispatch({ type: 'SET_FILTER', payload: { filter: 'all' } });
       }

      setPosts(postsData)
      setNetworkError(null)
    } catch (error) {
      console.error('Error fetching posts:', error);
      const errorMessage = 'Please check your internet connection and try again!';
      setNetworkError(errorMessage);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchPosts();
  }, [active]);

  const onRefresh = async () => {
    setRefreshing(true);
    setRefreshing(false);
    await fetchPosts();
  };

  const getItemCount = useCallback((data) => data.length, []);
  const getItem = useCallback((data, index) => data[index], []);
  const renderVirtualizedItem = useCallback(({ item }) => (
    <Snippet post={item} postId={item.id} />
  ), []);

  const handleFetchMorePosts = () => {


  }

  // Backhandler only in the feed
  useEffect(() => {
    const backHandler = BackHandler.addEventListener(
      "hardwareBackPress",
      handleBackPress
    );
    return () => backHandler.remove();
  }, []);
  const handleBackPress = () => {
    if (navigation.canGoBack()) {
      navigation.goBack();
    } else {
      BackHandler.exitApp();
    }
    return true;
  };

  return (
    <View style={styles.feed}>
      <Topnav />
      <StatusBar barStyle={'dark-content'} backgroundColor={'#ffff'}/>
      {errorMessage && <NetworkError message={errorMessage} />}


      {/* <CampusModal /> */}
      <VirtualizedList
    showsVerticalScrollIndicator={false}
    data={posts}
    renderItem={renderVirtualizedItem}
    keyExtractor={(item) => item.id}
    getItemCount={getItemCount}
    getItem={getItem}
    onEndReached={handleFetchMorePosts}
    onEndReachedThreshold={0.5}
    contentContainerStyle={styles.flatListContent}
    ListHeaderComponent={<Search />}
    refreshControl={
      <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />    }
  />
      <Pressable style={styles.createbtn} onPress={() => navigation.navigate("CreatePost")} >
        <Image
          style={styles.vectorIcon1}
          resizeMode="cover"
          source={require("../assets/Create.png")}
        />
      </Pressable>

      {loading && 
      <View style={styles.loading}>
         <ActivityIndicator size={"small"} color={'#2696b8'}/>
      </View>}
    </View>
  );
};


const styles = StyleSheet.create({
  flatListContent: {
    flexDirection: "column",
    marginTop: 3, paddingBottom: 142,
  },
  userProfile:{
    // position:'absolute', 
    zIndex: 2, top: 65, left: 10,
    flexDirection: 'row',
  },
  userProfileImage:{
    width: 30, height: 30,
  },
  names:{
    flexDirection: 'column', marginLeft: 10,
  },
  uname:{
    fontSize: 14, fontWeight: 500,
  },
  feedFlexBox: {
    flex: 1,
    overflow: "hidden",
  },
  headerPosition: {
    left: 0,
    position: "absolute",
  },
  vectorIconLayout: {
    height: 20,
    width: 20,
  },
  flatList: {
    marginTop: 3,
    overflow: "hidden",
  },
  scrollview: {
    top: 101,
    height: 688,
    alignItems: "center",
  },
  bottomview: {
    top: 788,
  },
  vectorIcon1: {
    bottom: 70, right: 20,
    width: 50,
    height: 50,
  },
  createbtn: {
    zIndex: 2, 
    // position: 'absolute', 
  },
  header: {
    top: 0,
  },
  feed: {
    backgroundColor: "#f8f8f8",
    width: "100%",
    height: 852,
    overflow: "hidden",
  },
  loading:{
    position: 'absolute', 
    left: 0, right: 0, top: 60, bottom: 0, 
    backgroundColor: 'white',
    alignItems: 'center', justifyContent: 'center'

  }
});

export default Feed;
