import { createStore, combineReducers } from "redux";

const initialUserProfileState = {
  username: "",
  profileImage: "",
  field: "",
};

const initialCampusState = {
  campus: "my-campus",
  user: null,
};

const initialAttachmentState = {
  attachment: null,
};

const initialFetchPostsState = {
  batchNumber: 0,
};

const userProfileReducer = (state = initialUserProfileState, action) => {
  switch (action.type) {
    case "SET_USER_PROFILE":
      return {
        ...state,
        ...action.payload,
      };
    default:
      return state;
  }
};

const campusReducer = (state = initialCampusState, action) => {
  switch (action.type) {
    case "SET_CAMPUS_FILTER":
      console.log("Setting campus filter to", action.payload);
      return { ...state, campus: action.payload };
    default:
      return state;
  }
};

const attachmentReducer = (state = initialAttachmentState, action) => {
  switch (action.type) {
    case "SET_ATTACHMENT":
      return { ...state, attachment: action.payload };
    default:
      return state;
  }
};

const fetchPostsReducer = (state = initialFetchPostsState, action) => {
  switch (action.type) {
    case "SET_BATCH_NUMBER":
      return {
        ...state,
        batchNumber: action.payload,
      };
    default:
      return state;
  }
};

const rootReducer = combineReducers({
  userProfile: userProfileReducer,
  campus: campusReducer,
  attachment: attachmentReducer,
  fetchPosts: fetchPostsReducer,
});

const store = createStore(rootReducer);

export default store;
